import React, { useRef } from "react";
import { useGSAP } from "@gsap/react";
import { gsap } from "gsap";
import ThemeChanger from "./components/ThemeChanger";

const App = () => {
  const [open, setOpen] = React.useState(false);
  const themeChangerRef = useRef();

  useGSAP(() => {
    gsap.fromTo(themeChangerRef.current, { y: "-100%" }, { y: 0, duration: 0.5, ease: "power2.inOut" });
  });

  return (
      <div>
        <ThemeChanger open={open} setOpen={setOpen} ref={themeChangerRef} />
        <header>
          <a href="#">logo</a>
          <ul>
            <li><a href="#">home</a></li>
            <li><a href="#">work</a></li>
            <li><a href="#">contact</a></li>
          </ul>
        </header>
      </div>
  );
}

export default App;
