import React from "react";
import ColorOption from "./ColorOption";

const ThemeChanger = ({ open, setOpen }) => {

	const handleButtonClick = () => {
		setOpen(!open);
	};

	return (
			<div className={`colorChanger ${open && 'open p-4'} relative`}>
				<div className='flex items-center justify-center gap-2'>
					<ColorOption/>
					<ColorOption/>
					<ColorOption/>
					<ColorOption/>
					<ColorOption/>
					<ColorOption/>
					<ColorOption/>
				</div>
				<button onClick={handleButtonClick} className='absolute top-3 right-3 transition ease-in-out delay-150 duration-300 hover:rotate-270'>
					<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
						<path strokeLinecap="round" strokeLinejoin="round" d="M6 18 18 6M6 6l12 12"/>
					</svg>
				</button>
			</div>
	);
};

export default ThemeChanger;
