import React from 'react';
import {useTheme} from "../context/ThemeContext";

const ColorOption = ({item}) => {
	const {setTheme, themes} = useTheme()

	return (
			<button
					onClick={() => setTheme(item)}
					className='themepicker-btn active cursor-pointer'
					activeClassName='active'
			>
				<p className='themepicker-text'>{item}</p>
				<div className='themepciker-palette'>
					<span className='theme-picker-color theme-picker-color--primary'></span>
					<span className='theme-picker-color theme-picker-color--secondary'></span>
					<span className='theme-picker-color theme-picker-color--border'></span>
					<span className='theme-picker-color theme-picker-color--textoffset'></span>
					<span className='theme-picker-color theme-picker-color--text'></span>
				</div>
			</button>
	);
};

export default ColorOption;
