import React, {useEffect, useRef} from 'react';
import {useGSAP} from "@gsap/react";

const Hero = () => {
	const titleRef = useRef(null);
	const pretitleRef = useRef(null);
	const textRef = useRef(null);
	const {fadeIn} = useGSAP();

	useEffect(() => {
		fadeIn(titleRef.current, {duration: 0.5, y: 20, opacity: 0});
		fadeIn(pretitleRef.current, {duration: 0.5, y: 20, opacity: 0, delay: 0.3});
		fadeIn(textRef.current, {duration: 0.5, y: 20, opacity: 0, delay: 0.6});
	}, [fadeIn]);

	return (
			<div className="hero__inner">
				<h1 ref={titleRef} className="hero__title">I make websites.</h1>
				<p ref={pretitleRef} className="hero__pretitle"><a href="#">Hello, my name is Ilya</a></p>
				<div className='hero__content'>
					<p ref={textRef} className='hero__text'>
						Iâ€™m a front-end developer. I also write about the
						web on my blog and elsewhere.
					</p>
				</div>
			</div>
	);
};

export default Hero;
