import React from 'react';
import Banner from "./Banner";
import site1 from "../img/1.png";
import site2 from "../img/Screenshot2.png";

const MyWork = () => {
	const [sites, setSites] = React.useState([
		{
			title: "WebSite1",
			siteImg: '../img/1.png',
			desc: 'thi si one site',
			createdAt: '12:45'
		},
		{
			title: "WebSite2",
			siteImg: '../img/Screenshot2.png',
			desc: 'thi si one site',
			createdAt: '12:45'
		},
	]);
	const [currSite, setCurrSite] = React.useState({})
	console.log(currSite)
	return (
			<section className='work__section'>
				<div className='container'>
					<h2 className='section__title'>My work</h2>
					<Banner/>
					<div className="work__list-outer">
						<ul className='work__list'>
							{sites.map((site, index) => (
									<li className='work__item' key={site.title} onClick={() => setCurrSite(index)}>
										<p className='work__item-title'>{site.title}</p>
										<img className='work__img' src={site.siteImg} alt="current website"/>
									</li>
							))}
						</ul>
					</div>
				</div>
			</section>
	);
};

export default MyWork;
