import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const ThemeChanger = ({ open, setOpen }) => {
	const themeChangerRef = useRef(null);

	useEffect(() => {
		if (open) {
			gsap.to(themeChangerRef.current, { height: "auto", duration: 0.5, ease: "power2.inOut" });
		} else {
			gsap.to(themeChangerRef.current, { height: 0, duration: 0.5, ease: "power2.inOut" });
		}
	}, [open]);

	const handleButtonClick = () => {
		setOpen(!open);
	};

	return (
			<div
					ref={themeChangerRef}
					className={`bg-blue-400 py-10 px-4 overflow-hidden`}
					style={{ height: open ? "auto" : 0 }}
			>
				THEME CHANGER
				{/* Button for toggling open state */}
				{/* onClick вызывает функцию handleButtonClick, которая изменяет состояние open */}
				<button onClick={handleButtonClick}>
					<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
						<path strokeLinecap="round" strokeLinejoin="round" d="M6 18 18 6M6 6l12 12"/>
					</svg>
				</button>
			</div>
	);
};

export default ThemeChanger;
